- /*/v2/api-docs\n      - /csrf\n\n# 激活Sentinel对Feign的支持\nfeign:\n  sentinel:\n    enabled: true', '587002a674105dca204e281a0e9fede2', '2020-05-14 14:17:55', '2023-09-02 10:23:59', 'nacos', '172.19.0.1', '', '', '网关模块', 'null', 'null', 'yaml', '', '');
